const messages=document.querySelector(".messages");window.addEventListener("load",()=>{setTimeout(()=>{messages.classList.length>1&&messages.classList.remove(messages.classList[1])},2e3)});const btnToggle=document.querySelector("#btn-sidebar");btnToggle&&btnToggle.addEventListener("click",()=>{document.querySelector(".sidebar").classList.toggle("small")});class Tabs{constructor(e,s,t,i){this.selectedTab=0,this.formLogin=e,this.formRegister=s,this.loginTab=t,this.registerTab=i}activeTab(e){1===e?(this.loginTab.classList.remove("tab-active"),this.registerTab.classList.add("tab-active"),this.formLogin.classList.remove("active"),this.formRegister.classList.add("active")):(this.loginTab.classList.add("tab-active"),this.registerTab.classList.remove("tab-active"),this.formRegister.classList.remove("active"),this.formLogin.classList.add("active"))}init(){this.loginTab&&this.registerTab&&(this.loginTab.addEventListener("click",()=>{this.selectedTab=0,this.activeTab(this.selectedTab)}),this.registerTab.addEventListener("click",()=>{this.selectedTab=1,this.activeTab(this.selectedTab)}))}}const TAB=new Tabs(document.querySelector("#form-login"),document.querySelector("#form-register"),document.querySelector("#tab-login"),document.querySelector("#tab-register"));TAB.init();